mapper.update:
UPDATE menu
SET menu_pic = #{menuPic}
WHERE menu_pk = #{menuPk}

mapper.select:
SELECT menu_name
FROM menu
WHERE menu_res_pk = #{menuResPk};

SELECT menu_res_pk AS menuResPk
FROM menu
WHERE menu_pk = #{menuPk};

mapper.delete:
DELETE
FROM menu
WHERE menu_pk = #{menuPk}
AND menu_res_pk = #{menuResPk}

mapper.select:
SELECT res_pk
FROM restaurant
WHERE res_user_pk = #{resUserPk}
LIMIT 1;

SELECT res_pk AS resPk
FROM restaurant
WHERE res_user_pk = #{resUserPk}
LIMIT 1;
