mapper.select:
SELECT COUNT(review_pk) AS reviewCount
FROM review
WHERE res_pk = #{resPk};

SELECT format(AVG(review_rating) , 1) AS reviewAvg
FROM review
WHERE res_pk = #{resPk};

SELECT SUM(order_price) AS monthSales
FROM done_order
WHERE DATE_FORMAT(created_at, '%Y-%m') = #{date}
AND res_pk = #{resPk}
AND done_order_state = 1;

SELECT count(res_pk) AS monthOrderCount
FROM done_order
WHERE DATE_FORMAT(created_at, '%Y-%m') = #{date}
AND res_pk = #{resPk}
AND done_order_state = 1;

SELECT sum(order_price) AS dailySales
FROM done_order
WHERE DATE_FORMAT(created_at, '%Y-%m-%d') = #{date}
AND res_pk = #{resPk}
AND done_order_state = 1;

SELECT count(res_pk) AS dailyOrderCount
FROM done_order
WHERE DATE_FORMAT(created_at, '%Y-%m-%d') = #{date}
AND res_pk = #{resPk}
AND done_order_state = 1;

SELECT res_pk
FROM restaurant
WHERE res_pk = #{resPk}
AND res_user_pk = #{resUserPk};
