mapper.insert:
INSERT into userGetRes
SET user_id = #{userId}
, user_pw = #{userPw}
, user_name = #{userName}
, user_nickname = #{userNickname}
, user_pic = #{userPic}
, user_phone = #{userPhone}
, user_email = #{userEmail}
, user_role = #{userRole}
, user_login_type = #{userLoginType}

mapper.update:
UPDATE userGetRes
SET user_pic = #{picName}
WHERE user_pk = #{signedUserPk}

UPDATE userGetRes
SET user_pw = #{newPw}
WHERE user_pk = #{signedUserPk}

UPDATE userGetRes
SET user_state = 3
WHERE user_pk = #{userPk}

UPDATE restaurant
SET restaurant_state = 3
WHERE res_user_pk = #{userPk}

mapper.select:
SELECT user_pic AS userPic
FROM userGetRes
WHERE user_pk = #{userPK}
